class AdminWrapper extends Component {
    componentWillMount() {
        this.check_role()
    }
    check_role() {
        if(this.props.user.role !== 'admin')
            this.props.router.replace('/unauthorized')
    }
    render() {
        return (
            <div>
                {this.props.children}
            </div>
        )
    }
}

let mapStateToProps = state => {
    return {
        user: state.user
    }
}

export default connect(mapStateToProps)(AdminWrapper)
