import {
  DataGrid,
} from "@mui/x-data-grid";
import React, { useContext, useEffect, useState } from "react";
import { Context } from "../../index";
import {toJS} from "mobx";

const getRenderCell = ({field, value}) => {
    if(field === "link"){
        return <a target="_blank"  rel="noopener noreferrer" href={value}>Профиль</a>;
    }
};

const CuratorTable1 = ({ rows, columns }) => {
  return (
    <div style={{ height: 300, width: "100%" }}>
      <DataGrid rows={rows} columns={columns} />
    </div>
  );
};

const CuratorTableData = () => {
  const [rows, setRows] = useState([]);
  const [columns, setColumns] = useState([]);
  const { store } = useContext(Context);

  useEffect(() => {
      console.log(toJS(store.curatorData))
    store
      .getCuratorTable(store.flowData, store.curatorData)
      .then((response) => {
        const requiredDataFromResponse = response;
        const columns_data = requiredDataFromResponse[0].map(
          (eachSensorItem) => ({
            headerName: eachSensorItem.Header,
            field: eachSensorItem.accessor,
            renderCell: getRenderCell,
            editable: eachSensorItem.editable,
          })

        );
        console.log(requiredDataFromResponse[1]);
        const rows_data = requiredDataFromResponse[1].map((eachSensorItem) => ({
          id: eachSensorItem.first_name,
          first_name: eachSensorItem.first_name,
          last_name: eachSensorItem.last_name,
          email: eachSensorItem.email,
          chat: eachSensorItem.chat,
          link: eachSensorItem.link,
          col1: eachSensorItem.cols.col1,
          col2: eachSensorItem.cols.col2,
        }));
        setColumns(columns_data);
        setRows(rows_data);
      })
      .catch((error) => {
        setColumns([]);
        setRows([]);
      });
  }, []);

  return (
    <div>
      <CuratorTable1 rows={rows} columns={columns} />
    </div>
  );
};

export default function CuratorTable12() {
  return <CuratorTableData />;
}
